from django import template
from django.templatetags.static import (
    do_static as _do_static, static as _static,
)

register = template.Library()


def static(path):
    # Backwards compatibility alias for django.templatetags.statics.statics().
    # Deprecation should start in Django 2.0.
    return _static(path)


@register.tag('statics')
def do_static(parser, token):
    # Backwards compatibility alias for django.templatetags.statics.do_static().
    # Deprecation should start in Django 2.0.
    return _do_static(parser, token)
